"use strict";(self.webpackChunkguide_to_ux_tests=self.webpackChunkguide_to_ux_tests||[]).push([[6414],{4759:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Introduction","href":"/ux-tests-guide/","docId":"intro","unlisted":false},{"type":"category","label":"Getting Started","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"What is UX testing?","href":"/ux-tests-guide/getting-started/what-is-ux-testing","docId":"getting-started/what-is-ux-testing","unlisted":false},{"type":"link","label":"Moderated vs. Non-Moderated","href":"/ux-tests-guide/getting-started/moderated-non-moderated","docId":"getting-started/moderated-non-moderated","unlisted":false},{"type":"link","label":"When to Use Non-Moderated Tests","href":"/ux-tests-guide/getting-started/when-to-use-non-moderated-tests","docId":"getting-started/when-to-use-non-moderated-tests","unlisted":false}],"href":"/ux-tests-guide/category/getting-started"},{"type":"category","label":"Planning UX Tests","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Defining Test Objectives","href":"/ux-tests-guide/planning/defining-objectives","docId":"planning/defining-objectives","unlisted":false},{"type":"link","label":"Identifying Target Participants","href":"/ux-tests-guide/planning/choosing-participants","docId":"planning/choosing-participants","unlisted":false},{"type":"link","label":"Structuring Test Scenarios","href":"/ux-tests-guide/planning/structuring-scenarios","docId":"planning/structuring-scenarios","unlisted":false},{"type":"link","label":"Writing Test Tasks","href":"/ux-tests-guide/planning/writing-tasks","docId":"planning/writing-tasks","unlisted":false}],"href":"/ux-tests-guide/category/planning-ux-tests"},{"type":"category","label":"Questionnaire Design","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Crafting Questions","href":"/ux-tests-guide/questionnaire/crafting-questions","docId":"questionnaire/crafting-questions","unlisted":false},{"type":"link","label":"Types of questions","href":"/ux-tests-guide/questionnaire/question-types","docId":"questionnaire/question-types","unlisted":false},{"type":"link","label":"Common Pitfalls to Avoid","href":"/ux-tests-guide/questionnaire/pitfalls-to-avoid","docId":"questionnaire/pitfalls-to-avoid","unlisted":false},{"type":"link","label":"Examples of Questions","href":"/ux-tests-guide/questionnaire/examples-quest","docId":"questionnaire/examples-quest","unlisted":false}],"href":"/ux-tests-guide/category/questionnaire-design"},{"type":"category","label":"Test Types","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Navigation & Search","href":"/ux-tests-guide/test-types/navigation-search","docId":"test-types/navigation-search","unlisted":false},{"type":"link","label":"Usability of Key Features","href":"/ux-tests-guide/test-types/usability-testing","docId":"test-types/usability-testing","unlisted":false},{"type":"link","label":"Comparative Interface Testing","href":"/ux-tests-guide/test-types/comparative-testing","docId":"test-types/comparative-testing","unlisted":false},{"type":"link","label":"Process Testing (e.g., user flows)","href":"/ux-tests-guide/test-types/examples-quest","docId":"test-types/examples-quest","unlisted":false},{"type":"link","label":"Testing Content Understanding","href":"/ux-tests-guide/test-types/content-understanding","docId":"test-types/content-understanding","unlisted":false}],"href":"/ux-tests-guide/category/test-types"},{"type":"category","label":"Conducting the Test","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Instructions for Participants","href":"/ux-tests-guide/conducting/participant-instructions","docId":"conducting/participant-instructions","unlisted":false},{"type":"link","label":"Best Practices","href":"/ux-tests-guide/conducting/best-practices","docId":"conducting/best-practices","unlisted":false},{"type":"link","label":"Handling Technical Issues","href":"/ux-tests-guide/conducting/handling-issues","docId":"conducting/handling-issues","unlisted":false}],"href":"/ux-tests-guide/category/conducting-the-test"},{"type":"category","label":"Analyzing Results","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Organizing Data","href":"/ux-tests-guide/analyzing-results/organizing-data","docId":"analyzing-results/organizing-data","unlisted":false},{"type":"link","label":"Identifying key patterns and insights","href":"/ux-tests-guide/analyzing-results/identifying-insights","docId":"analyzing-results/identifying-insights","unlisted":false},{"type":"link","label":"Prioritizing Findings","href":"/ux-tests-guide/analyzing-results/prioritizing-findings","docId":"analyzing-results/prioritizing-findings","unlisted":false},{"type":"link","label":"Examples of Data Visualization","href":"/ux-tests-guide/analyzing-results/visualization-examples","docId":"analyzing-results/visualization-examples","unlisted":false}],"href":"/ux-tests-guide/category/analyzing-results"},{"type":"category","label":"Reporting Findings","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"UX Report Structure","href":"/ux-tests-guide/reporting-findings/report-structure","docId":"reporting-findings/report-structure","unlisted":false},{"type":"link","label":"Communicating Insights","href":"/ux-tests-guide/reporting-findings/communicating-insights","docId":"reporting-findings/communicating-insights","unlisted":false},{"type":"link","label":"Recommendations for Action","href":"/ux-tests-guide/reporting-findings/actionable-recom","docId":"reporting-findings/actionable-recom","unlisted":false}],"href":"/ux-tests-guide/category/reporting-findings"},{"type":"category","label":"Case Studies","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Real-world Examples of Successful UX Testing","href":"/ux-tests-guide/case-studies/real-world-examples","docId":"case-studies/real-world-examples","unlisted":false},{"type":"link","label":"Lessons Learned","href":"/ux-tests-guide/case-studies/lessons-learned","docId":"case-studies/lessons-learned","unlisted":false}],"href":"/ux-tests-guide/category/case-studies"},{"type":"category","label":"Resources","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Checklist for UX Test","href":"/ux-tests-guide/resources/test-checklist","docId":"resources/test-checklist","unlisted":false},{"type":"link","label":"FAQs","href":"/ux-tests-guide/resources/faqs","docId":"resources/faqs","unlisted":false}],"href":"/ux-tests-guide/category/resources"}]},"docs":{"analyzing-results/identifying-insights":{"id":"analyzing-results/identifying-insights","title":"Identifying key patterns and insights","description":"Once your UX test data is organized, the next step is to identify the key patterns and insights hidden within it. This process involves connecting the dots between user behaviors, feedback, and outcomes to uncover trends that can inform design improvements.","sidebar":"tutorialSidebar"},"analyzing-results/organizing-data":{"id":"analyzing-results/organizing-data","title":"Organizing Data","description":"Before diving into insights, it\u2019s essential to organize your UX test data systematically. Proper organization allows you to uncover patterns, prioritize findings, and make sense of raw feedback. Think of this as setting the stage for meaningful analysis.","sidebar":"tutorialSidebar"},"analyzing-results/prioritizing-findings":{"id":"analyzing-results/prioritizing-findings","title":"Prioritizing Findings","description":"Not all insights carry the same weight. Some issues are critical showstoppers, while others are minor inconveniences. Prioritizing your findings ensures that your team focuses on the most impactful improvements, maximizing your resources and delivering the greatest value to users.","sidebar":"tutorialSidebar"},"analyzing-results/visualization-examples":{"id":"analyzing-results/visualization-examples","title":"Examples of Data Visualization","description":"Great insights deserve great visuals. A well-crafted chart or graph can transform dense data into a story that even the most design-averse stakeholders can grasp. From task completion rates to heatmaps, this section will show you how to make your findings look as sharp as your research skills.","sidebar":"tutorialSidebar"},"case-studies/lessons-learned":{"id":"case-studies/lessons-learned","title":"Lessons Learned","description":"If there\u2019s one thing the high-stakes world of FinTech and banking teaches us, it\u2019s this: UX testing is your insurance policy against frustrated users and missed opportunities. Whether you\u2019re simplifying a loan application or redesigning fraud alerts, every test is a treasure trove of lessons. Here are the standout takeaways from the trenches of UX success.","sidebar":"tutorialSidebar"},"case-studies/real-world-examples":{"id":"case-studies/real-world-examples","title":"Real-world Examples of Successful UX Testing","description":"When it comes to FinTech and banking, user experience isn\u2019t just a \u201cnice-to-have\u201d\u2014it\u2019s the difference between building trust and losing customers. These industries deal with complex workflows, sensitive data, and high stakes, making UX testing critical. Let\u2019s dive into some real-world examples of how UX testing has driven success in these fields.","sidebar":"tutorialSidebar"},"conducting/best-practices":{"id":"conducting/best-practices","title":"Best Practices","description":"Running a UX test is more than following a script\u2014it\u2019s about creating an environment where participants feel comfortable and your team gathers meaningful insights. Whether you\u2019re conducting moderated or non-moderated tests, these best practices will help you get the most out of your efforts.","sidebar":"tutorialSidebar"},"conducting/handling-issues":{"id":"conducting/handling-issues","title":"Handling Technical Issues","description":"Technical glitches can happen to even the best-prepared UX researchers. While they\u2019re frustrating, a calm and methodical response ensures they don\u2019t derail the test or damage the participant\u2019s experience. This section provides strategies to handle technical issues like a pro.","sidebar":"tutorialSidebar"},"conducting/participant-instructions":{"id":"conducting/participant-instructions","title":"Instructions for Participants","description":"Clear, concise instructions set the stage for a successful UX test. They guide participants through the test process, ensuring they understand what\u2019s expected of them and feel confident as they engage with your product. This section covers how to craft instructions that foster ease and focus without biasing user behavior.","sidebar":"tutorialSidebar"},"getting-started/moderated-non-moderated":{"id":"getting-started/moderated-non-moderated","title":"Moderated vs. Non-Moderated","description":"When it comes to UX testing, you\u2019ve got two main options: moderated and non-moderated. Both are powerful tools, but like a hammer versus a screwdriver, each has its time and place. Let\u2019s break them down so you can wield the right one with confidence.","sidebar":"tutorialSidebar"},"getting-started/what-is-ux-testing":{"id":"getting-started/what-is-ux-testing","title":"What is UX testing?","description":"UX testing, short for User Experience testing, is the process of evaluating a product or service by observing real users as they interact with it. The goal is simple yet critical: uncover how people experience your design\u2014what delights them, what frustrates them, and, most importantly, what prevents them from achieving their goals.","sidebar":"tutorialSidebar"},"getting-started/when-to-use-non-moderated-tests":{"id":"getting-started/when-to-use-non-moderated-tests","title":"When to Use Non-Moderated Tests","description":"Non-moderated UX tests are the workhorses of user research: efficient, scalable, and ideal for capturing authentic user behavior. But they\u2019re not a one-size-fits-all solution. Knowing when to deploy this method is key to maximizing its value.","sidebar":"tutorialSidebar"},"intro":{"id":"intro","title":"Home","description":"Welcome to the ultimate playbook for unmoderated UX testing\u2014a must-have for designers, product managers, and developers who are serious about creating products that actually work for users. Think of this as your secret weapon to outsmart clunky interfaces, baffling user flows, and \\"just click everything until it works\\" moments.","sidebar":"tutorialSidebar"},"planning/choosing-participants":{"id":"planning/choosing-participants","title":"Identifying Target Participants","description":"The right participants can make or break your UX test. They\u2019re the key to uncovering actionable insights that truly represent your audience. This section helps you pinpoint the people who matter most for your research and ensures your findings are both relevant and impactful.","sidebar":"tutorialSidebar"},"planning/defining-objectives":{"id":"planning/defining-objectives","title":"Defining Test Objectives","description":"Clear objectives are the foundation of any successful UX test. Without them, you risk gathering data that\u2019s interesting but ultimately unhelpful. Objectives focus your testing efforts, ensuring every task and question aligns with your goals.","sidebar":"tutorialSidebar"},"planning/structuring-scenarios":{"id":"planning/structuring-scenarios","title":"Structuring Test Scenarios","description":"A well-structured test scenario is the backbone of effective UX testing. It provides the context and tasks participants need to interact with your product naturally and deliver meaningful insights. This section walks you through crafting scenarios that mimic real-world usage while aligning with your objectives.","sidebar":"tutorialSidebar"},"planning/writing-tasks":{"id":"planning/writing-tasks","title":"Writing Test Tasks","description":"Test tasks are the heartbeat of a UX test\u2014they direct participants\' actions and reveal how effectively your product supports their goals. Well-written tasks provide clarity, focus, and actionable insights. This section will teach you how to craft tasks that guide participants while letting their natural behavior shine.","sidebar":"tutorialSidebar"},"questionnaire/crafting-questions":{"id":"questionnaire/crafting-questions","title":"Crafting Questions","description":"Crafting effective questions is an art and a science. The way you phrase your questions can shape the quality of your feedback, influencing how participants respond and what insights you gain. This section provides a blueprint for creating questions that are clear, focused, and aligned with your UX testing goals.","sidebar":"tutorialSidebar"},"questionnaire/examples-quest":{"id":"questionnaire/examples-quest","title":"Examples of Questions","description":"The quality of your UX test insights depends on the questions you ask. This section provides examples of effective questions tailored to various scenarios, helping you design questionnaires that gather both qualitative and quantitative data.","sidebar":"tutorialSidebar"},"questionnaire/pitfalls-to-avoid":{"id":"questionnaire/pitfalls-to-avoid","title":"Common Pitfalls to Avoid","description":"Creating effective UX test questionnaires can be tricky, and even small missteps can compromise the quality of your data. To ensure you get meaningful and actionable insights, watch out for these common pitfalls:","sidebar":"tutorialSidebar"},"questionnaire/question-types":{"id":"questionnaire/question-types","title":"Types of questions","description":"A well-rounded UX questionnaire includes a mix of question types to gather diverse and actionable insights. From quantitative data to rich qualitative feedback, each type of question plays a unique role in understanding user behavior, preferences, and pain points. Here\u2019s a breakdown of the essential question types and how to use them effectively:","sidebar":"tutorialSidebar"},"reporting-findings/actionable-recom":{"id":"reporting-findings/actionable-recom","title":"Recommendations for Action","description":"Insights without action are like a roadmap without a destination\u2014they look impressive but don\u2019t get you anywhere. Your recommendations are where the magic happens, translating findings into tangible steps that improve the user experience and deliver measurable results. Let\u2019s make them count.","sidebar":"tutorialSidebar"},"reporting-findings/communicating-insights":{"id":"reporting-findings/communicating-insights","title":"Communicating Insights","description":"Presenting UX insights is part art, part science, and all about getting your audience to sit up and pay attention. It\u2019s not just about sharing what you found\u2014it\u2019s about delivering it in a way that sparks action. Here\u2019s how to communicate your insights like the UX boss you are.","sidebar":"tutorialSidebar"},"reporting-findings/report-structure":{"id":"reporting-findings/report-structure","title":"UX Report Structure","description":"A killer UX report isn\u2019t just a data dump\u2014it\u2019s a well-structured narrative that transforms raw findings into a compelling story. Think of it as your chance to shine, delivering insights that grab attention, drive decisions, and (let\u2019s be real) make you look like the UX rockstar you are.","sidebar":"tutorialSidebar"},"resources/faqs":{"id":"resources/faqs","title":"FAQs","description":"Got questions about UX testing? Don\u2019t worry\u2014we\u2019ve got answers. Whether you\u2019re new to the game or a seasoned pro looking for clarity, this FAQ section is here to tackle your burning questions with wit and wisdom. Let\u2019s dive in!","sidebar":"tutorialSidebar"},"resources/test-checklist":{"id":"resources/test-checklist","title":"Checklist for UX Test","description":"Ready to dive into your UX test? Hold on, superstar\u2014let\u2019s make sure you\u2019ve covered all the bases first. This checklist is your secret weapon for running a test that\u2019s smooth, professional, and packed with actionable insights. Whether you\u2019re a seasoned pro or a UX newbie, this list ensures you\u2019re ready to wow.","sidebar":"tutorialSidebar"},"test-types/comparative-testing":{"id":"test-types/comparative-testing","title":"Comparative Interface Testing","description":"When you\u2019re deciding between multiple design options, why guess when you can let your users decide? Comparative interface testing helps you evaluate which design resonates better with your audience by comparing usability, aesthetics, and overall experience. It\u2019s the ultimate tiebreaker in your design process.","sidebar":"tutorialSidebar"},"test-types/content-understanding":{"id":"test-types/content-understanding","title":"Testing Content Understanding","description":"Clear, concise, and compelling content is the unsung hero of user experience. Whether it\u2019s a headline, call-to-action, or instructional text, your content must communicate effectively to guide users through your product. Testing content understanding evaluates how well your messaging resonates with users and supports their goals.","sidebar":"tutorialSidebar"},"test-types/examples-quest":{"id":"test-types/examples-quest","title":"Process Testing (e.g., user flows)","description":"User flows are the journeys your users take to achieve their goals within your product. From onboarding to checkout, these processes should feel intuitive, efficient, and frustration-free. Process testing evaluates these flows step by step, ensuring every interaction aligns with user expectations.","sidebar":"tutorialSidebar"},"test-types/navigation-search":{"id":"test-types/navigation-search","title":"Navigation & Search","description":"Navigation and search are the backbone of a seamless user experience. If users can\u2019t find what they\u2019re looking for quickly and intuitively, frustration sets in\u2014and so does abandonment. This section focuses on how to test and optimize these critical components to ensure your product is easy to explore.","sidebar":"tutorialSidebar"},"test-types/usability-testing":{"id":"test-types/usability-testing","title":"Usability of Key Features","description":"Your product\u2019s key features are its defining strengths\u2014the tools and functions users rely on to accomplish their goals. If these features are clunky, confusing, or fail to meet expectations, they can quickly undermine the entire user experience. This section focuses on how to evaluate and optimize the usability of your core features.","sidebar":"tutorialSidebar"}}}}')}}]);